package lia.util.Pathload.util;

/**
 * This is a remote exception. Any error generated by the client
 * in the PathloadServlet is thrown as a PathloadException in the client.
 * 
 * Usually when a response from the servlet includes a message and
 * an error status then a exception is thrown, the Exception message
 * is the message from the servlet
 * 
 * @author heri
 *
 */
public class PathloadException extends Exception {

	/**
	 * Default serial for class
	 */
	private static final long serialVersionUID = -6734374593859365225L;

	/**
	 * Default constructor
	 */
	public PathloadException() {
		super();
	}

	/**
	 * Default constructor
	 * @param message		Mesage to be passed
	 */
	public PathloadException(String message) {
		super(message);
	}

	/**
	 * Default constructor
	 * @param message	Message to be passed
	 * @param cause		Cause for error
	 */
	public PathloadException(String message, Throwable cause) {
		super(message, cause);
	}

	/**
	 * Default constructor
	 * @param cause	Cause for error
	 */
	public PathloadException(Throwable cause) {
		super(cause);
	}

}
